package RiteshPrPractice;

public class Trial6Forloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		for(int i=50; i<=100; i++ ){
    if (i%2==0)
                {
			System.out.println("Even Number" +i);
                }
    else
               {
    	   System.out.println("Odd Number" +i );
	            }
	}
	    
	}
}
